import React, {Component} from 'react';
import axios from 'axios';
import jsPDF from "jspdf";


export default class DMarksView extends Component{
    constructor(props){
        super(props);
        this.state={
            mark:{}

        };

    }


    
GeneratePDF =()=>{
    var doc = new jsPDF("p", "pt", "a2", "pdf");
    doc.html(document.querySelector('#content'),{
           callback: function(pdf){
               pdf.save("mypdf.pdf");
           }
    });
  };

    componentDidMount(){ 
        const id = this.props.match.params.id;

        axios.get(`/dmark2/${id}`).then((res)=>{ 
            if(res.data.success){
                this.setState({
                    mark:res.data.dmark2
                });

                console.log(this.state.mark);
            }

        });
    }
    render(){



        const{S1DMark1,S1DMark2,S1DMark3,S1DMark4,S1DMark5,S1DTotal,S1DAllocatedMarks,S2DMark1,S2DMark2,S2DMark3,S2DMark4,S2DMark5,S2DTotal,S2DAllocatedMarks,
            S3DMark1,S3DMark2,S3DMark3,S3DMark4,S3DMark5,S3DTotal,S3DAllocatedMarks,  S4DMark1,S4DMark2,S4DMark3,S4DMark4,S4DMark5,S4DTotal,S4DAllocatedMarks, 
           Name,DWeight,S1ID,S2ID,S3ID,S4ID } = this.state.mark;



     
        return(
           
            <div className="backgroud4" >
                
         
                  <div id="content" style={{marginTop:'20px' }}>
                    <h4 className="textcenter" > {Name}</h4>
                  
                     <hr/> 

  <center>
        
                     <table style={{  border:' 1px solid #ddd',textAlign: 'left',  borderCollapse: 'collapse',
  width: '100%'}}>
  <tr>
    <th style={{   backgroundColor: '#04AA6D',color: 'white',   textalign: 'left',padding: '15px',border:' 1px solid #ddd'}}>Student ID</th>
    <th style={{   backgroundColor: '#04AA6D',color: 'white',   textalign: 'left',padding: '15px',border:' 1px solid #ddd'}}>Marks</th>
    <th style={{   backgroundColor: '#04AA6D',color: 'white',  textalign: 'left',padding: '15px',border:' 1px solid #ddd'}}></th>
  </tr>
  <tr>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>{S1ID}</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>{S1DMark1}</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}></td>

  </tr>
  <tr>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>{S2ID}</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>{S2DMark2}</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}></td>
   
  </tr>
  <tr>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>{S3ID}</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>{S3DMark3}</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}></td>

  </tr>
  <tr>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>{S4ID}</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>{S4DMark4}</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}></td>
   
</tr>

<tr>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>Total Marks</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>{S1DTotal}</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}></td>
   
</tr>
<tr>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>Total Marks</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>{S2DTotal}</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}></td>
   
</tr>
<tr>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>Total Marks</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>{S3DTotal}</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}></td>
   
</tr>
<tr>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>Total Marks</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>{S4DTotal}</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}></td>
   
</tr>
<tr>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>Weight</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}>{DWeight}%</td>
    <td style={{padding: '15px',border:' 1px solid #ddd',textAlign: 'left',backgroundColor:'#f2f2f2'}}></td>
   
</tr>

</table>
        </center>

                 

</div>
<br></br><br></br>

<button className="btn3 button3" onClick={this.GeneratePDF} type="primary">Print the Marking Scheme</button>
</div>


              
                 )
                     }
                    }
