import React, {Component} from 'react';
import axios from 'axios'; 



export default class TopicDocS extends Component{
constructor(props){
  super(props);

 
  this.state={
    topics:[] 
  };
}


componentDidMount(){
  this.retriveTopics(); 
}


retriveTopics(){ 
  axios.get("/topicdocs").then(res=>{ 
    if(res.data.success){
      this.setState({
        topics:res.data.existingTopicDocs
      });

      console.log(this.state.topics);
    }
  });
}





filterData(topic,searchKey){

  const result = topic.filter((a)=>

   a.DocName.includes(searchKey)
  )
  this.setState({topics:result})
}

handleSearchArea=(e) =>{
  const searchKey =e.currentTarget.value;
  axios.get("/topicdocs").then(res=>{ 
    if(res.data.success){
      this.filterData(res.data.existingTopicDocs,searchKey)

    }

});
}

render(){ 
    return(
    
      <div >
      <div className="row">
        



     








<center>
       <div class="w3-card-4 w3-margin w3-white"  style={{width:'1500px',height:'600px'}} >




       <div style={{marginTop:'6px'}}>

       <center>
           <h4 className="textcenter" style={{marginTop:'40px' ,fontFamily: 'Fantasy',fontSize:'30px' }}>Topic Feedback by Panel</h4>
           </center>
       <hr/>

<div class="w3-card-4 w3-margin w3-white"  style={{width:'1300px'}} >

<div class="w3-container w3-indigo" style={{width:'1200px'}}>




<h1>Notice</h1>
<h>Topic details document should be uploaded by students till it accepted by the panel member / If it is rejecter students have to select a new topic and send it to the supervisor, then send to the panel member</h>
</div>
</div>
</div>




     
             <button type="button" class="btn btn-primary" ><a href = "/topicDeRej"  style={{textDecoration:'none', color:'white'}}> Feedback from Supervisor</a></button>  <br></br><br></br>

   
      

             <div class="dropdown"> 
<button class="dropbtn">Feed back from panel member</button>
{this.state.topics.map((topics,index)=>(
 

<div class="dropdown-content">
<tr key={index} >
   <th scope="row">{index+1}</th>  
          
<a href="/topicDeRej" >{topics.DocName} {topics.Action}ed  {topics.Reason}</a>
         
</tr>
         </div>    
       
      
         ) )}
             
             </div>

         



            
  

       


</div>


</center>



        </div>
 
      </div>
       
     
    )
  }
}
