import React, {Component} from 'react';
import axios from 'axios'; 



export default class DMarksView extends Component{
constructor(props){
  super(props);

 
  this.state={
    topics:[] 
  };
}


componentDidMount(){
  this.retriveTopics(); 
}


retriveTopics(){ 
  axios.get("/dmarks2").then(res=>{ 
    if(res.data.success){
      this.setState({
        topics:res.data.existingDmarks2
      });

      console.log(this.state.topics);
    }
  });
}





filterData(topic,searchKey){

  const result = topic.filter((a)=>

   a.GroupId.includes(searchKey)
  )
  this.setState({topics:result})
}

handleSearchArea=(e) =>{
  const searchKey =e.currentTarget.value;
  axios.get("/dmarks2").then(res=>{ 
    if(res.data.success){
      this.filterData(res.data.existingDmarks2,searchKey)

    }

});
}

render(){ 
    return(

      
        <center>
  <div class="card bg-light mb-3" style={{width:'1200px'}}>

      <div>
      <div >
      <div className="row">
        



      
           <h4 className="textcenter" style={{marginTop:'40px' ,fontFamily: 'Fantasy',fontSize:'30px' }}>Mark Sheet</h4>
      
       <hr/>
       <center>
       <div style={{marginTop:'10px'}}>

<div class="w3-card-4 w3-margin w3-purple"  style={{width:'1200px'}} >

<div class="w3-container w3-deep-purple" style={{width:'1100px'}}>




<h1 style={{backgroundColor:'blue',width:'400px'}}></h1>
<h></h>
</div>
</div>
</div>
</center>

       
     
        <div className="col-lg-3 mt-2 mb-2">
          
        <input
           className = "from-control"  style={{width:'400px', height:'40px',backgroundColor:'white'}}type="search" placeholder="Search by Group ID" name="searchQuery" onChange={this.handleSearchArea}>
        </input>
            
             </div>
           
      </div>



      <div style={{marginTop:'20px' }}>
     
   
        
       
        </div>

      
<center>
        <table id="customers" className="table table-hover" style={{marginTop:'40px',backgroundColor:'white'}} > 
          <thead>
            <tr >
            <th scope="col">Index</th>
              <th scope="col">Group ID</th>
              <th scope="col">Student ID</th>
              <th scope="col">Marks</th>
             
             
            </tr>
          </thead>

          <tbody>
              {this.state.topics.map((topics,index)=>( //used salaries array variable
                <tr key={index} style={{}}>
                  <td>{index+1}</td>  
                  <th>
                      
                      
              
                            {topics.Name}
                        
                            
                  
                   </th>  
                   <td>
                       
                   {topics.S1ID}
                   {topics.S2ID}
                   {topics.S3ID}
                   {topics.S4ID}

                  </td>
                   {topics.S1AllocatedMarks}
                   {topics.S2AllocatedMarks}
                   {topics.S3AllocatedMarks}
                   {topics.S4AllocatedMarks}
                  
              
                  </tr> 
                
                 ) )}



               </tbody>
            </table>
        </center>

       





        </div>
      </div>
       
      </div>
      </center>
  
       
     
    )
  }
}
