import { useState } from "react";
import { useHistory } from "react-router-dom";
import React from 'react';
const AddVideoc = () => {
  const history = useHistory();
  const [name, setName] = useState("");

  const [video, setVideo] = useState([]);

  // const handleChange = (e) => {
  //   const newStateFiles = [...files, e.target.files];
  //   setFiles(newStateFiles);
  // };
  const upload = async (e) => {
    try {
      e.preventDefault();
      // let formData = new FormData();
      // for (let i = 0; i < files.length; i++) {
      //   formData.append("files", files[i]);
      // }
      // formData.append("name", name);
      // formData.append("video", data.video);
      // formData.append("pdf", data.pdf);

      const data = new FormData();

      data.append("name", name);

      for (var x = 0; x < video.length; x++) {
        data.append("uploaded_Image", video[x]);
      }



      const res = await fetch(`http://localhost:5000/videoc`, {
        method: "POST",
        body: data,
      });
      if (res.ok) {
        setName("");

        setVideo(null);
        history.replace("/");
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div style={{ maxWidth: 500, margin: "auto" }}>
      {/* <pre>{file!=null && file.length}</pre> */}
