import React from 'react';
import PropTypes from 'prop-types';
import { connect } from '../react-rdux/react-rdux';

import { checkLogItem } from '../actions';

const Log = ({ text, checked, index, check }) => {
  return (text
    ? <li onClick={() => check(index)}>
        {checked ? <s>{text}</s> : text}
      </li>
    : null
  );
};

Log.propTypes = {
  index: PropTypes.number,
  text: PropTypes.string,
  checked: PropTypes.bool,
  remove: PropTypes.func,
};

function mapStateToProps(state, props) {
  return {
    text: `state.log.logs[${props.index}].text`,
    checked: `state.log.logs[${props.index}].checked`,
  };
}

export default connect(
  mapStateToProps,
  {
    check: checkLogItem,
  }
)(Log);
