import React from 'react';
import { render } from 'react-dom';
import { bindActionCreators } from 'rdux';
import { Provider } from './react-rdux/react-rdux';

import App from './components/App';
import './index.html';
import configureStore from './store';
import {
  addLog,
  setTime,
} from './actions';

const store = configureStore();

const { addLog: boundAddLog } = bindActionCreators({ addLog }, store.dispatch);

boundAddLog('Init app');

let seconds = 0;

const interval = setInterval(() => {
  store.dispatch(setTime((new Date()).getTime()));
  seconds += 1;
  if (!(seconds % 2)) {
    boundAddLog('+2 seconds');
  }
}, 1000);

boundAddLog('Init interval');

render(
  <Provider store={store}>
    <App/>
  </Provider>,
  document.getElementById('root')
);
